mapper.select:
select

,

from tb_information
where title LIKE #{name,jdbcType=VARCHAR}
AND two_classify_code = #{twoClassifyCode,jdbcType=VARCHAR}
ORDER BY update_time DESC

mapper.update:
UPDATE tb_information
SET flag = "1"
where information_code = #{informationCode,jdbcType=VARCHAR}

mapper.insert:

insert into tb_information (information_id, information_code, title,
publish_data, user_code, two_classify_code,
information_state, cover_img, create_time,
update_time, flag, content
)
values (#{informationId,jdbcType=VARCHAR}, #{informationCode,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR},
#{publishData,jdbcType=TIMESTAMP}, #{userCode,jdbcType=VARCHAR}, #{twoClassifyCode,jdbcType=VARCHAR},
#{informationState,jdbcType=VARCHAR}, #{coverImg,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP},
#{updateTime,jdbcType=TIMESTAMP}, #{flag,jdbcType=VARCHAR}, #{content,jdbcType=LONGVARCHAR}
)
